import React, { useRef, useState } from 'react';
import './App.css';

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [capturedImage, setCapturedImage] = useState(null);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
    } catch (err) {
      console.error('Erro ao acessar a câmera:', err);
    }
  };

  const captureImage = () => {
    const context = canvasRef.current.getContext('2d');
    context.drawImage(videoRef.current, 0, 0, 640, 480);
    const imageDataURL = canvasRef.current.toDataURL('image/png');
    setCapturedImage(imageDataURL);
  };

  return (
    <div className="container">
      <h1>Verificação de Imagem</h1>
      <p>Por favor, capture sua imagem para verificar sua identidade.</p>

      <div className="camera-container">
        <video ref={videoRef} autoPlay playsInline></video>
        <canvas ref={canvasRef} width="640" height="480" style={{ display: 'none' }}></canvas>
      </div>

      <div className="buttons">
        <button onClick={startCamera}>Ativar Câmera</button>
        <button onClick={captureImage}>Capturar Imagem</button>
      </div>

      {capturedImage && (
        <div className="preview">
          <h2>Imagem Capturada:</h2>
          <img src={capturedImage} alt="Captura" />
        </div>
      )}
    </div>
  );
}

export default App;
