import React, { useRef, useState } from 'react';
import './App.css';
import { FaCamera, FaCheckCircle } from 'react-icons/fa';

function App() {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [capturedImage, setCapturedImage] = useState(null);
  const [isCameraActive, setIsCameraActive] = useState(false);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      setIsCameraActive(true);
    } catch (err) {
      console.error('Erro ao acessar a câmera:', err);
    }
  };

  const captureImage = () => {
    const context = canvasRef.current.getContext('2d');
    context.drawImage(videoRef.current, 0, 0, 640, 480);
    const imageDataURL = canvasRef.current.toDataURL('image/png');
    setCapturedImage(imageDataURL);
  };

  return (
    <div className="container">
      <h1>Verificação de Identidade</h1>
      <p>Capture sua imagem para verificação e segurança.</p>

      <div className="camera-box">
        {isCameraActive ? (
          <video ref={videoRef} autoPlay playsInline muted />
        ) : (
          <div className="placeholder">
            <FaCamera size={60} color="#9ca3af" />
            <p>Ative sua câmera</p>
          </div>
        )}
        <canvas ref={canvasRef} width="640" height="480" style={{ display: 'none' }}></canvas>
      </div>

      <div className="buttons">
        {!isCameraActive && (
          <button className="btn start" onClick={startCamera}>
            <FaCamera style={{ marginRight: '8px' }} />
            Ativar Câmera
          </button>
        )}
        {isCameraActive && (
          <button className="btn capture" onClick={captureImage}>
            <FaCheckCircle style={{ marginRight: '8px' }} />
            Capturar Imagem
          </button>
        )}
      </div>

      {capturedImage && (
        <div className="preview">
          <h2>Imagem Capturada:</h2>
          <img src={capturedImage} alt="Captura" />
        </div>
      )}
    </div>
  );
}

export default App;
